def lstm_lm_small(vocab, drop_scale=1.):
    '''
    lstm_lm_small - small LSTM_LM model

    Inputs:

    - `vocab Vocab`: vocab to use

    - `drop_scale float`: scale dropout values
    '''

    d_vocab = len(vocab.itos)
    bos_idx = vocab.stoi['bos']
    d_embedding = 256
    d_hidden = 1024
    n_layers = 3
    bidir = False
    tie_weights = True

    input_dropout = 0.3*drop_scale
    lstm_dropout = 0.3*drop_scale

    model = LSTM_LM(d_vocab,
                    d_embedding,
                    d_hidden,
                    n_layers,
                    input_dropout,
                    lstm_dropout,
                    bos_idx,
                    bidir,
                    tie_weights)

    return model

def lstm_lm_large(vocab, drop_scale=1.):
    '''
    lstm_lm_large - large LSTM_LM model

    Inputs:

    - `vocab Vocab`: vocab to use

    - `drop_scale float`: scale dropout values
    '''

    d_vocab = len(vocab.itos)
    bos_idx = vocab.stoi['bos']
    d_embedding = 400
    d_hidden = 1552
    n_layers = 5
    bidir = False
    tie_weights = True


    input_dropout = 0.3*drop_scale
    lstm_dropout = 0.3*drop_scale

    model = LSTM_LM(d_vocab,
                    d_embedding,
                    d_hidden,
                    n_layers,
                    input_dropout,
                    lstm_dropout,
                    bos_idx,
                    bidir,
                    tie_weights)

    return model

